import React from 'react';
import { FaCamera } from 'react-icons/fa';
import { ContextProvider } from "../src/global/Context";
import "./App.css";
const Create = () => {
    const {create} = React.useContext(ContextProvider);
    const [title, settitle] = React.useState('');
    const [image, setimage] = React.useState('');
    const handIn=(e)=>{
        setimage(e.target.files[0]);
    }
    const createPost=(e)=>{
        e.preventDefault();
        create({title, image});
        settitle('');
        setimage("");
    }
    return (
        <div className="create">
            <form onSubmit={createPost}>
              <div className="create__input">
                  <input type="text" className="createinput" placeholder="post a photo" onChange={(e)=> settitle(e.target.value)} value={title} />
              </div>
            
            <div className="create__second">
                <div className="create__second_a">
                    <label htmlFor="file">
                        <FaCamera className="camera" />
                    </label>
                    <input type="file" className="file" id="file" onChange={handIn} />
                </div>
                <div className="create__second_b">
                   <input type="submit" value="create" className="btn-submit" />
                </div>
            </div>
            </form>
        </div>
    )
}

export default Create;
